const clickerFunction = function (event, id, taskId) {
	const form = $APP.getForm(taskId);
	const control = form.getControl(id);
	const singleton = JQ.Widget[control.type].Singleton;

  localStorage.setItem('curr_date', control.param_bindings.Value.value);

	if (singleton.getDisabled(control) || singleton.getReadOnly(control)) {
		return;
	}

	const $text_field = singleton.getJQElement(control).find('.text_field.date');

	if ($text_field.data('pickmeup-options')) {
		return;
	}

	singleton.initField(control);
	js.stop_event(event);
};

JQ.Widget.DateInput.Singleton.Handlers.onDatePickerClick = clickerFunction;
JQ.Widget.Datetime.Singleton.Handlers.onDatePickerClick = clickerFunction;

