scriptData.script:
//АРМ_пользователя

udm:(прикладные_роли_пользователя := AVAILABLEROLES());
udm:(подразделение_пользователя := Политика_безопасности:Назначение_пользователей:по_пользователю(пользователь).подразделение[0]);

if (udm:(енд(пункт_меню))) {
    if(udm:("CL.CL.АРМ_видимость_дашборда" ? прикладные_роли_пользователя)){
        udm:(пункт_меню := 'main');
    } else { 
        udm:(пункт_меню := 'tasks'); 
    }
}

udm:(пункт_меню_тип := Основной_интерфейс:Пункты_меню:по_системному_имени(пункт_меню)[0]);
//udm:(форма_для_просмотра := пункт_меню_тип.ссылка_на_форму);
udm:(имя_раздела := пункт_меню_тип.наименование_пункта);

if (udm:(пункт_меню = 'substitution' и не ("CL.CL.АРМ_Замещение" ? прикладные_роли_пользователя))) {
    udm:(пункт_меню := 'tasks');
    udm:(пункт_меню_тип := Основной_интерфейс:Пункты_меню:по_системному_имени(пункт_меню)[0]);
    udm:(форма_для_просмотра := пункт_меню_тип.ссылка_на_форму);
    udm:(имя_раздела := пункт_меню_тип.наименование_пункта);
}

if (udm:(пункт_меню ? ['curr_app', 'curr_task'])) {
    if (udm:(Результат ? ["задача_работа", "просмотр", 'просмотр_заявки', 'только_просмотр_заявки'])) {
        if (udm:(Результат ? ["просмотр_заявки", "только_просмотр_заявки"])) {
            udm:(форма_для_просмотра := Операционный_фронт:Форма_типа_задачи:по_типу_задачи_формы_этапу(НД(), заявка.этап, false)[0].путь_формы);
        } else { // ищем форму по типу задачи
            udm:(форма_для_просмотра := Операционный_фронт:Форма_типа_задачи:по_типу_задачи_формы_этапу(задача.тип_задачи, НД(), false)[0].путь_формы);
        }
    } else if (udm:(Результат = "в_работу")) {
        udm:(форма_для_просмотра := пункт_меню_тип.ссылка_на_форму);
    }
}

udm:(назначение_пользователя := Политика_безопасности:Назначение_пользователей:по_пользователю(пользователь)[not ifnull(подразделение)]);
if(udm:("CL.CL.Общий_доступ" ? прикладные_роли_пользователя)){
    udm:(подчиненные := Политика_безопасности:Пользователь:пользователи_назначенные());
}else if(udm:("CL.CL.Задача_полномочия_региональный" ? прикладные_роли_пользователя and "CL.CL.Принадлежит_ДКК" ? прикладные_роли_пользователя)){
    udm:(текущее_подразделение := назначение_пользователя[0].подразделение);
    udm:(подчиненные := Политика_безопасности:Пользователь:пользователи_подразделения(текущее_подразделение));
} else {
    udm:(подчиненные := Политика_безопасности:Пользователь:по_пользователю_оргструктуре(пользователь));
}    
if(udm:("CL.CL.Задача_общий_пул" ? прикладные_роли_пользователя и не("CL.CL.Общий_доступ" ? прикладные_роли_пользователя))){
    //определяем подразделение и всех пользователей в рамках дерева подразделений
    udm:(пользователи_задачи := Политика_безопасности:Пользователь:пользователи_дерева_подразделений(назначение_пользователя[0].подразделение));
    udm:(подчиненные := пользователи_задачи);
} else {
    udm:(пользователи_задачи := подчиненные);
}

//Главная_задача

udm:(нужно_сохранение := false);
udm:(только_просмотр := Результат ? ["просмотр", 'просмотр_заявки', 'только_просмотр_заявки']);

if (udm:(пункт_меню_тип.Видимость.вкладки_контейнер_заявки)) {
    //<< определение набора вкладок
    if (udm:(not ifnull(заявка))) {
        if (udm:(заявка.этап.код ? ['заведение_заявки', 'пэ', 'подготовка_код', 'подготовка_сделка', 'сделка', 'сопровождение'])) {
            udm:(набор_вкладок := Операционный_фронт:Вкладка_этапа:по_форме_этапу(форма_для_просмотра, заявка.этап, нд()));
        } else {
            udm:(набор_вкладок := Операционный_фронт:Вкладка_этапа:по_форме_этапу(форма_для_просмотра, Кредитная_заявка:Этап_рассмотрения:по_коду('экспертиза')[0], нд()));
        }
    } else {
        udm:(набор_вкладок := Операционный_фронт:Вкладка_этапа:по_форме_этапу(форма_для_просмотра, нд(),задача.тип_задачи));
    }
udm:(count(набор_вкладок) > 0) && udm:(выбранная_вкладка := набор_вкладок[порядковый_номер = 1][0]); 
//>> определение набора вкладок
}

//if (udm:(пункт_меню_тип.Видимость.вкладки_задачи)) {
    if(!udm:(только_просмотр)){
        //udm:(активные_задачи := Операционный_фронт:Активная_задача:по_пользователю(пользователь)[not(этот.задача.тип_задачи.код ? ["акцептПР", "предварительный_анализ"])]);
        //udm:(выбранная_задача := активные_задачи[этот.задача = задача][0]);
        udm:(параметры_сессии.задача_работа := true);
    } else {
        udm:(параметры_сессии.задача_работа := false);
    }
//}

if (udm:(пункт_меню_тип.Видимость.группа_кнопок_функц)) {

    if (udm:(Результат = 'только_просмотр_заявки')) {
        udm:(пункт_меню := "curr_app");
        udm:(имя_раздела := "Заявка №" + заявка.номер);
    } else if (udm:(Результат = 'просмотр_заявки')) {
        udm:(пункт_меню := "curr_app");
        udm:(имя_раздела := "Заявка №" + заявка.номер);
    } else { // ЗАДАЧИ
        udm:(пункт_меню := "curr_task");
        udm:(имя_раздела := "Задача №" + задача.номер);
    }
} //конец "if (udm:(пункт_меню_тип.Видимость.группа_кнопок_функц))"


//Просмотр_процесса_заявки


if (udm:(пункт_меню_тип.Видимость.окно_процесса)) {

    udm:(параметры_сессии.заявка_работа := true);
    //udm:(не ЕНД(выбранный_экземпляр.заявка) и (заявка := выбранный_экземпляр.заявка, true)); >>PRGNBKK-348
    udm:(имя_раздела := "Заявка №" + заявка.номер + '\n' + заявка.анкета.краткое_наименование);

    if (udm:(заявка.этап.код ? ['заведение_заявки', 'пэ', 'подготовка_код', 'подготовка_сделка', 'сделка', 'сопровождение'])) {
        udm:(набор_вкладок := Операционный_фронт:Вкладка_этапа:по_этапу(заявка.этап));
    } else { //для всех других этапов одинаковый набор вкладок
        udm:(набор_вкладок := Операционный_фронт:Вкладка_этапа:по_этапу(Кредитная_заявка:Этап_рассмотрения:по_коду('экспертиза')[0]));
    }

    udm:(выбранная_вкладка := набор_вкладок[порядковый_номер = 1][0]);
}

// Главная_карточка_группы

if (udm:(пункт_меню == 'group_card')) {
    udm:(имя_раздела := "Карточка группы:\n" + группа_компаний.полное_наименование);
    udm:(предыдущая_версия := группа_компаний);
}

if (
    udm:(
        count(набор_вкладок) > 0 
        и не енд(заявка) 
        и не енд(заявка.решение) 
        и не енд(заявка.решение.пользователь) 
        и не (заявка.этап.код ? ['подготовка_код', 'подготовка_сделка', 'сделка'])
    )
) {
    udm:(добавить(набор_вкладок, Операционный_фронт:Вкладка_этапа:по_коду('рул')[0]));
}
udm:(рул := (задача.тип_задачи.код == 'решение_ул'));
if (udm:(рул)) udm:(выбранная_вкладка := набор_вкладок[порядковый_номер = 13][0]);

if (
    udm:(
        count(набор_вкладок) > 0 
        и не енд(заявка) 
        и (заявка.тип_заявки.код == 'транш') 
        и не енд(заявка.родительская_заявка) 
        и (заявка.этап.код ? ['заведение_заявки', 'подготовка_сделка', 'сделка'])
        и не (Операционный_фронт:Вкладка_этапа:по_коду('родительская_заявка')[0] ? набор_вкладок)
    )
) {
    udm:(добавить(набор_вкладок, Операционный_фронт:Вкладка_этапа:по_коду('родительская_заявка')[0]));
}

udm:(отправка_на_доработку := false);

udm:(видимость_окна_процесса := пункт_меню_тип.Видимость.окно_процесса и не (нет_процесса или (пользователь != параметры.пользователь)) и енд(форма_для_просмотра));
udm:(выбранная_задача := задача);

variables.defaultValue:
(видимость_окна_процесса) => {
    if (видимость_окна_процесса) { //Просмотр_процесса_заявки

        item = 'curr_app';
        if (!!FORM.attr("выбранный_экземпляр")) {
            /*
            imp - вкладки с долгой загрузкой, не входит первая вкладка
            загруженные_вкладки - массив для обращения к загруженным вкладкам для каждых открытых параметров_процесса
            curr_app - текущий параметры процесса, дополненные хар-ми формы{tab - номер текущей вкладки, text_fin - текст уведомления для КЗ, tabs - уже показанные вкладки}
            считаем не загруженными только imp
            */
            var imp = ['клиент', 'кредитное_заключение', 'анкеты_участников'];
            if (!!FORM.attr("набор_вкладок")) {
                for (var i=0; i<FORM.attr("набор_вкладок").length; i++) {
                    var item = FORM.attr("набор_вкладок").items[i].content;
                    var ld = (imp.indexOf(item.код) == -1);
                    загруженные_вкладки[загруженные_вкладки.length] = {num: item.порядковый_номер, code: item.код, load: ld}; 
                }
            }
            curr_app = FORM.attr("выбранный_экземпляр").content;
            curr_app.tab = 1;
            curr_app.text_fin = '';
            curr_app.button_label = '';
            curr_app.tabs = загруженные_вкладки;
            app_tabs[app_tabs.length] = curr_app;
        }

    } else { ////Главная_задача
        if (!FORM.attr("только_просмотр")) {
            if (!!выбранная_задача) {
                curr_app = выбранная_задача.content;
                curr_app.tab = 1;
                app_tabs.push(curr_app);
            }
        } else { 
            if (!!FORM.attr("набор_вкладок") && FORM.attr("набор_вкладок").length > 0) {
                /*
                imp - вкладки с долгой загрузкой, не входит первая вкладка
                загруженные_вкладки - массив для обращения к загруженным вкладкам для каждых открытых параметров_процесса
                curr_app - текущий параметры процесса, дополненные хар-ми формы{tab - номер текущей вкладки, text_fin - текст уведомления для КЗ, tabs - уже показанные вкладки}
