} else if (код_подразделения == 'кп') { //переназначение КМ
	var curExecutor = udm:(заявка.клиентский_менеджер);
	udm:(заявка.клиентский_менеджер := inExecutor);
	тип_задачи_создание = 'предварительный_анализ';
	тип_задачи_переназначение = ['предварительный_анализ'];
} else if (код_подразделения == 'бэк') { //переназначение сопровождения
	var curExecutor = udm:(заявка.специалист_сопровождения);
	udm:(заявка.специалист_сопровождения := inExecutor);
	тип_задачи_создание = 'подготовка_код';
	тип_задачи_переназначение = ['подготовка_код', 'подготовка_код_правки_клиента'];
} else { // переназначение КА
	var curExecutor = udm:(заявка.кредитный_аналитик);
	udm:(заявка.кредитный_аналитик := inExecutor);
	тип_задачи_создание = 'андеррайтинг';
	тип_задачи_переназначение  = ['андеррайтинг'];
}


if (!!curExecutor && udm:(js:Политика_безопасности:пользователь(curExecutor) != inExecutor)) { //При переназначении заявки
	var tasksNotEnds = udm:(Задача:Задача:все_невыполненные_по_заявке_типу_исполнителю(заявка, Задача:Тип_задачи:актуальные_по_коду(js:string[](тип_задачи_переназначение)), js:Политика_безопасности:пользователь(curExecutor)));
	if(tasksNotEnds && tasksNotEnds.length > 0){ //Переназначим существующие задачи
		tasksNotEnds.forEach(function(task){
			udm:(Задача:назначить_задачу(js:Задача:Задача(task), inExecutor, js:Задача:Задача(task).срок_исполнения, 'Задача назначена системой'));
			task.распределитель = udm:(пользователь);
			var active_task = udm:(Операционный_фронт:Активная_задача:по_задаче(js:Задача:Задача(task))[0]);
			(active_task) && (active_task.пользователь = null);
		});
	}

	var param_push = {
		subject: "Переназначение заявки №" + udm:(заявка.номер),
		text: "Руководитель " + udm:(пользователь.фио) + " переназначил заявку №" + udm:(заявка.номер) + " на " + udm:(inExecutor.фио),
		image: 'flag',
		user_login: curExecutor.логин,
		data: {}
	}
	udm:(Общее:send_push(js:variant(param_push)));

} else if (код_подразделения == 'кп' || код_подразделения == 'дкк') { //Создадим задачи на прескоринг, андеррайтинг, код, если их еще не было
	if (udm:(епусто(Задача:Задача:по_заявке_типу_задачи(заявка, Задача:Тип_задачи:актуальные_по_коду(js:string(тип_задачи_создание)))))) { //задачи не было, надо создать
		//надо пропихнуть заявки по процессу, чтобы там создались задачи

		// Назначаем текущий процесс и подпроцессы на пользователя взявшего в работу
		var lgr = new logger(false, 'klay_app.log');
