                udm:(runprocess("Основной_интерфейс.Процессы.Системные.Отправка_сообщения_заявка", ["заявка"], заявка));
            }

            //Завершает этап прескоринга и переходим далее по БП, процесс висит на блоке ожидания
            if (код_подразделения == 'дкк' && udm:(заявка.этап.код == 'экспертиза' и заявка.статус.код == 'экспертизы' and FINDINSTANCE(js:string(procId)).processname = "Предварительная_экспертиза")){
				udm:(Система.Операционный_фронт.Процессы.Интерфейсные.$процессы.Предварительная_экспертиза.$экземплярыпроцессов[идэкземпляра == js:string(procId)].$объект.Результат := 'Назначение');
                udm:(runprocess("Основной_интерфейс.Процессы.Системные.Отправка_сообщения_заявка", ["заявка"], заявка));
            }
        }
	}
} else if (код_подразделения == 'бэк') {
	udm:(Задача_заявки:создать_назначить_задачу(заявка.специалист_сопровождения, 'Требуется подготовить КОД', 'Начало подготовки КОД', Задача:Тип_задачи:актуальные_по_коду(js:string(тип_задачи_создание)), заявка, пользователь));
}

udm:(Операционный_фронт:Параметры_рассмотрения_заявки:по_заявке(заявка)[0].пользователь := inExecutor);
//<<отправка уведомления
var param = {
    code_subject : 'назначение_заявки',
    code_text : 'назначение_заявки',
    address: [udm:(inExecutor.email)],
    user_login : udm:(inExecutor.логин)
}
udm:(Кредитная_заявка:email_cred_app(заявка, js:variant(param)));
//>>отправка уведомления

udm:(заявка.срок_рассмотрения 	:= timeofdate(inTerm));
udm:(заявка.комментарий 			:= inComment);
if(udm:(заявка.этап.код = 'заведение_заявки')){
	if(udm:(inDoPreExp)){
		udm:(заявка.вид_рассмотрения := Кредитная_заявка:Вид_рассмотрения:по_коду('стандартное')[0]);
	} else{ 
		udm:(заявка.вид_рассмотрения := Кредитная_заявка:Вид_рассмотрения:по_коду("упрощенное")[0]);
	}
}
udm:(Кредитная_заявка:создание_истории_заявки(заявка, inComment ,заявка.срок_рассмотрения, пользователь, inExecutor));
var param_proc_app = udm:(Операционный_фронт:Параметры_рассмотрения_заявки:по_заявке(заявка)[0]);
param_proc_app.пользователь = udm:(inExecutor);
var process_tree = udm:(SUBPROCESSTREE(js:string(param_proc_app.ид_процесса), 0).procInstId);
for (var i=0; i<process_tree.length; i++){
	udm:(FINDINSTANCE(js:string(process_tree[i])).назначения.Все := FINDUSER(inExecutor.логин));
