let observer = new MutationObserver(
  recs => {
    recs.forEach(
      rec => {
        // console.log('OBSERVER', rec);
        if (rec.addedNodes.length > 0 && rec.addedNodes[0].classList !== undefined && rec.addedNodes[0].classList.contains('pickmeup')) {
          const nav = rec.addedNodes[0].querySelector('.pmu-instance nav:first-child');
          const check = nav.children.length;

          const curr_date = localStorage.getItem('curr_date');
          let monthName;
          let yearName;
          if (String(curr_date) === 'null') {
            monthName = document.createTextNode(new Date().getMonthName());
            yearName = document.createTextNode(String(new Date().getFullYear()));
          } else {
            const month = months.filter(item => item.month_code === curr_date.split('.')[1])[0];
            monthName = document.createTextNode(month.month_name);
            yearName = document.createTextNode(curr_date.split('.')[2]);
          }
          if (check === 2) {
            const month = document.createElement("div");
            month.classList.add('pmu-month', 'pmu-button');
            month.appendChild(monthName);
            nav.appendChild(month);

            const year = document.createElement("div");
            year.classList.add('pmu-year', 'pmu-button');
            year.appendChild(yearName);
            nav.appendChild(year);

            const next = document.createElement("div");
            next.classList.add('pmu-next', 'pmu-button');
            next.style.visibility = 'visible';
            nav.appendChild(next);
          }
        }
      }
    )    
  }
);

observer.observe(
